sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue == '') {
      return;
   }

   //get the value of What needed field
	var whatneeded = g_form.getValue('u_what_needed');

	// clear the options of What needed field
	g_form.clearOptions('u_what_needed');

	// if Request type field value is Hr then add the folllowing options in What needed field. 
	if(newValue == 'hr') {
		g_form.addOption('u_what_needed', 'hr1', 'Human resource1');
		g_form.addOption('u_what_needed', 'hr2', 'Human resource2');
		g_form.addOption('u_what_needed', 'other', 'Other');

	}

	// if Request type field value is Facilities then add the folllowing options in What needed field.

	if(newValue == 'facilities') {
		g_form.addOption('u_what_needed', 'facilities1', 'Facilities1');
		g_form.addOption('u_what_needed', 'facilities2', 'Facilities2');
		g_form.addOption('u_what_needed', 'other', 'Other');
	}


	// if Request type field value is Leagal then add the folllowing options in What needed field.

	if(newValue == 'legal') {
		g_form.addOption('u_what_needed', 'legal1', 'Legal1');
		g_form.addOption('u_what_needed', 'legal2', 'Legal2');
		g_form.addOption('u_what_needed', 'other', 'Other');
	}

	// if the  form is loaded and record is not new then the set What needed field value based on the Request type field value before it was loaded.

	if(isLoading && !g_form.isNewRecord()) {
		g_form.setValue('u_what_needed', whatneeded);
	}

}
